import axios from "axios";
import { apiKey } from "../constants";

// endpoints
const apiBaseUrl = "https://api.themoviedb.org/3";
const trendingMoviesEndpoint = `${apiBaseUrl}/trending/movie/day?api_key=${apiKey}`;
const upcomingMoviesEndpoint = `${apiBaseUrl}/movie/upcoming?api_key=${apiKey}`;
const topRatedMoviesEndpoint = `${apiBaseUrl}/movie/top_rated?api_key=${apiKey}`;
const searchMoviesEndpoint = `${apiBaseUrl}/search/movie?api_key=${apiKey}`;

//dynamic endpoints
const movieDetailsEndpoint = (movieId) =>
  `${apiBaseUrl}/movie/${movieId}?api_key=${apiKey}`;
const movieCreditsEndpoint = (movieId) =>
  `${apiBaseUrl}/movie/${movieId}/credits?api_key=${apiKey}`;
const similarMoviesEndpoint = (movieId) =>
  `${apiBaseUrl}/movie/${movieId}/similar?api_key=${apiKey}`;

const personDetailsEndpoint = (personId) =>
  `${apiBaseUrl}/person/${personId}?api_key=${apiKey}`;
const personMoviesEndpoint = (personId) =>
  `${apiBaseUrl}/person/${personId}/movie_credits?api_key=${apiKey}`;

// image paths
export const image500 = (path) =>
  path ? `https://image.tmdb.org/t/p/w500${path}` : null;
export const image342 = (path) =>
  path ? `https://image.tmdb.org/t/p/w342${path}` : null;
export const image185 = (path) =>
  path ? `https://image.tmdb.org/t/p/w185${path}` : null;
export const fallbackPersonImage =
  "data:image/png;base64,";

const apiCall = async (endpoint, params) => {
  const options = {
    method: "GET",
    url: endpoint,
    params: params ? params : {},
  };

  try {
    const response = await axios.request(options);
    return response.data;
  } catch (error) {
    console.error(error);
    return {};
  }
};

export const fetchTrendingMovies = () => {
  return apiCall(trendingMoviesEndpoint);
};

export const fetchUpcomingMovies = () => {
  return apiCall(upcomingMoviesEndpoint);
};

export const fetchTopRatedMovies = () => {
  return apiCall(topRatedMoviesEndpoint);
};

export const fetchMovieDetails = (movieId) => {
  return apiCall(movieDetailsEndpoint(movieId));
};

export const fetchMovieCredits = (movieId) => {
  return apiCall(movieCreditsEndpoint(movieId));
};

export const fetchSimilarMovies = (movieId) => {
  return apiCall(similarMoviesEndpoint(movieId));
};

export const fetchPersonDetails = (personId) => {
  return apiCall(personDetailsEndpoint(personId));
};

export const fetchPersonMovies = (personId) => {
  return apiCall(personMoviesEndpoint(personId));
};

export const searchMovies = (params) => {
  return apiCall(searchMoviesEndpoint, params);
};
